import './CampoTexto.css'

const CampoTexto = ({updateValue, value, label, required, placeholder}) => {


    const aoDigitar = (evento) => {
        updateValue(evento.target.value);

    }


    return (

        <div className="campo-texto">
            <label>{label}</label>
            <input value={value} onChange={aoDigitar} required={required} placeholder={placeholder} />  
        </div>

    )
}

export default CampoTexto
